    private void updateCatalogButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_updateCatalogButtonActionPerformed
        //Save
        if (ISBN.getText() == null || ISBN.getText().isEmpty()) {
            Client.initUI_popMessage("ISBN cannot be empty!");
            return;
        }
        String[] args = {ISBN.getText(), Title.getText(), Author.getText(), Publisher.getText(), PublishedYear.getText()};
        Client.updateCatalog(args);
    }//GEN-LAST:event_updateCatalogButtonActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        //Batch Process Pressed
        Client.batchUpdateCatalog();
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        //Auto Update Catalog pressed
        Client.autoGet(this, ISBN.getText());
    }//GEN-LAST:event_jButton2ActionPerformed

