    void clearStockInfo() {
        StockLabel.setText("");
        StockValue.setText("");
    }

    void fillCatalogInfo(String[] args) {
        ISBN.setText(args[0]);
        Title.setText(args[1]);
        Author.setText(args[2]);
        Publisher.setText(args[3]);
        PublishedYear.setText(args[4]);
    }

    void fillStockInfo(String[] args) {
        StockValue.setText(args[5]);
    }
